INSERT INTO `wp_ab_notifications` (`id`, `gateway`, `type`, `active`, `copy`, `subject`, `message`) VALUES
(1, 'email', 'client_pending_appointment', 0, 0, 'Your appointment information', '<p>Dear {client_name}.</p>\n<p>This is a confirmation that you have booked {service_name}.</p>\n<p>We are waiting you at {company_address} on {appointment_date} at {appointment_time}.</p>\n<p>Thank you for choosing our company.</p>\n<p>{company_name}<br />\n{company_phone}<br />\n{company_website}</p>\n'),
(2, 'email', 'client_pending_appointment_cart', 0, 0, 'Your appointment information', '<p>Dear {client_name}.</p>\n<p>This is a confirmation that you have booked the following items:</p>\n<p>{cart_info}</p>\n<p>Thank you for choosing our company.</p>\n<p>{company_name}<br />\n{company_phone}<br />\n{company_website}</p>\n'),
(3, 'email', 'staff_pending_appointment', 0, 0, 'New booking information', '<p>Hello.</p>\n<p>You have new booking.</p>\n<p>Service: {service_name}<br />\nDate: {appointment_date}<br />\nTime: {appointment_time}<br />\nClient name: {client_name}<br />\nClient phone: {client_phone}<br />\nClient email: {client_email}</p>\n'),
(4, 'email', 'client_approved_appointment', 0, 0, 'Your appointment information', '<p>Dear {client_name}.</p>\n<p>This is a confirmation that you have booked {service_name}.</p>\n<p>We are waiting you at {company_address} on {appointment_date} at {appointment_time}.</p>\n<p>Thank you for choosing our company.</p>\n<p>{company_name}<br />\n{company_phone}<br />\n{company_website}</p>\n'),
(5, 'email', 'client_approved_appointment_cart', 0, 0, 'Your appointment information', '<p>Dear {client_name}.</p>\n<p>This is a confirmation that you have booked the following items:</p>\n<p>{cart_info}</p>\n<p>Thank you for choosing our company.</p>\n<p>{company_name}<br />\n{company_phone}<br />\n{company_website}</p>\n'),
(6, 'email', 'staff_approved_appointment', 0, 0, 'New booking information', '<p>Hello.</p>\n<p>You have new booking.</p>\n<p>Service: {service_name}<br />\nDate: {appointment_date}<br />\nTime: {appointment_time}<br />\nClient name: {client_name}<br />\nClient phone: {client_phone}<br />\nClient email: {client_email}</p>\n'),
(7, 'email', 'client_cancelled_appointment', 0, 0, 'Booking cancellation', '<p>Dear {client_name}.</p>\n<p>You have cancelled your booking of {service_name} on {appointment_date} at {appointment_time}.</p>\n<p>Thank you for choosing our company.</p>\n<p>{company_name}<br />\n{company_phone}<br />\n{company_website}</p>\n'),
(8, 'email', 'staff_cancelled_appointment', 0, 0, 'Booking cancellation', '<p>Hello.</p>\n<p>The following booking has been cancelled.</p>\n<p>Service: {service_name}<br />\nDate: {appointment_date}<br />\nTime: {appointment_time}<br />\nClient name: {client_name}<br />\nClient phone: {client_phone}<br />\nClient email: {client_email}</p>\n'),
(9, 'email', 'client_rejected_appointment', 0, 0, 'Booking rejection', '<p>Dear {client_name}.</p>\n<p>Your booking of {service_name} on {appointment_date} at {appointment_time} has been rejected.</p>\n<p>Reason: {cancellation_reason}</p>\n<p>Thank you for choosing our company.</p>\n<p>{company_name}<br />\n{company_phone}<br />\n{company_website}</p>\n'),
(10, 'email', 'staff_rejected_appointment', 0, 0, 'Booking rejection', '<p>Hello.</p>\n<p>The following booking has been rejected.</p>\n<p>Reason: {cancellation_reason}</p>\n<p>Service: {service_name}<br />\nDate: {appointment_date}<br />\nTime: {appointment_time}<br />\nClient name: {client_name}<br />\nClient phone: {client_phone}<br />\nClient email: {client_email}</p>\n'),
(11, 'email', 'client_new_wp_user', 0, 0, 'New customer', '<p>Hello.</p>\n<p>An account was created for you at {site_address}</p>\n<p>Your user details:<br />\nuser: {new_username}<br />\npassword: {new_password}</p>\n<p>Thanks.</p>\n'),
(12, 'email', 'client_reminder', 0, 0, 'Your appointment at {company_name}', '<p>Dear {client_name}.</p>\n<p>We would like to remind you that you have booked {service_name} tomorrow on {appointment_time}. We are waiting you at {company_address}.</p>\n<p>Thank you for choosing our company.</p>\n<p>{company_name}<br />\n{company_phone}<br />\n{company_website}</p>\n'),
(13, 'email', 'client_follow_up', 0, 0, 'Your visit to {company_name}', '<p>Dear {client_name}.</p>\n<p>Thank you for choosing {company_name}. We hope you were satisfied with your {service_name}.</p>\n<p>Thank you and we look forward to seeing you again soon.</p>\n<p>{company_name}<br />\n{company_phone}<br />\n{company_website}</p>\n'),
